import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

function LoginPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showNotification, setShowNotification] = useState(false);


  const handleLogin = (e) => {
    e.preventDefault();
    if (username === 'askus' && password === 'nagiskus') {
      navigate('/main');
    } else {
      setShowNotification(true);
      setTimeout(() => setShowNotification(false), 3000);
    }
  };

  return (
    <StyledWrapper>
      <h3 style={{color:'#FE4A49',fontSize:'50px',marginBottom:'20px', marginTop:'10px'}}>TrackVie</h3>
      <div className="animated-border">
        <div id='container'>
          <h2 id='login'>Login</h2>
          <form onSubmit={handleLogin} id='form'>
            <input
              id='username'
              type="text"
              placeholder="Enter username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              id='password'
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button id='submitBtn' type="submit"><span>Log In</span></button>
          </form>
          <h1 style={{ fontSize: '18px', color: '#FE4A49' }}>or</h1>
          <button id='registerBtn'>register</button>
        </div>
      </div>
      <div id='notification-credentials' className='hidden'>
        <div><i class="fa-solid fa-circle-exclamation"></i></div>
        <div>Wrong Username or Password</div>
      </div>
    </StyledWrapper>
  );
}


