import styled from "styled-components";

function Card({ url }) {
  const openInNewTab = () => {
    window.open(url, "_blank");
  };
  const getDisplayName = (fullUrl) => {
    try {
      const hostname = new URL(fullUrl).hostname;
      return hostname.replace("www.", "").split(".")[0]; // e.g., "github"
    } catch {
      return "unknown";
    }
  };
  const changeIcons = (url)=>{
    if (url.includes('youtube')) {
      return <i class="fa-brands fa-youtube"></i>;
    }
    else

     return <i class="fa-solid fa-question"></i>;
  } 

  return (
    <StyledWrapper onClick={openInNewTab}>
      <div className="top">{changeIcons(url)}</div>
      <div className="bottom">
        <span className="icon">
          <i className="fa-solid fa-circle-play"></i>
        </span>
        <span className="showName">{getDisplayName(url)}</span>
      </div>
    </StyledWrapper>
  );
}

const StyledWrapper = styled.div`
  width: 220px;
  height: 150px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition:  0.3s;

  &:hover {
    background-color: #fe4a49;
    transform: scale(1.1);

  }

  .top, .bottom {
    font-size: 18px;
    margin: 3px;
    width: 100%;
    border: solid thin #fe4a49;
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    text-align: center;
    padding: 5px;
    overflow:hidden;
    background-color:#1d1d1d;
  }

  .icon {
    margin-right: 8px;
    color: #fe4a49;
  }
`;


export default Card;
