import styled from "styled-components";
import { useEffect, useState, useRef, useCallback } from "react";
import { collection, getDocs, query, orderBy, limit, startAfter } from "firebase/firestore";
import { db } from "/Users/bedrishwayze/Desktop/Freevie/trackvie/Web/src/firebase.js";
import Card from "./Card";

const PAGE_SIZE = 10; // Load 10 items at a time

function Homepage() {
  const [shows, setShows] = useState([]);
  const [lastDoc, setLastDoc] = useState(null);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  const observer = useRef();

  // Fetch shows with pagination
  const fetchShows = useCallback(async () => {
    if (loading || !hasMore) return;
    setLoading(true);

    try {
      const baseQuery = query(
        collection(db, "Users/id-2/savedShows"),
        orderBy("timestamp", "desc"), // Replace with a real field
        limit(PAGE_SIZE),
        ...(lastDoc ? [startAfter(lastDoc)] : [])
      );

      const snapshot = await getDocs(baseQuery);
      const newShows = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      setShows(prev => [...prev, ...newShows]);
      setLastDoc(snapshot.docs[snapshot.docs.length - 1]);
      setHasMore(snapshot.docs.length === PAGE_SIZE);
    } catch (error) {
      console.error("Failed to fetch shows:", error);
    } finally {
      setLoading(false);
    }
  }, [lastDoc, loading, hasMore]);

  useEffect(() => {
    fetchShows();
  }, [fetchShows]);

  // Observer to detect scrolling near the bottom
  const lastCardRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();

    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        fetchShows();
      }
    });

    if (node) observer.current.observe(node);
  }, [loading, hasMore, fetchShows]);

  return (
    <StyledWrapper>
      <div id="a">
        <div className="inside">
          <label className="burger" htmlFor="burger">
            <input type="checkbox" id="burger" />
            <span />
            <span />
            <span />
          </label>
        </div>
        <div className="inside" id="middle">
          <form action="">
            <div className="input-wrapper">
              <input type="text" name="searchbar" id="searchbar" />
              <i className="fa-solid fa-magnifying-glass"></i>
            </div>
          </form>
          <div className="little">k</div>
          <div className="little">l</div>
          <div className="little">m</div>
        </div>
        <div className="inside">z</div>
      </div>

      <div id="b">
        {shows.map((show, index) => {
          if (index === shows.length - 1) {
            return (
              <div key={show.id} ref={lastCardRef}>
                <Card url={show.url} />
              </div>
            );
          } else {
            return <Card key={show.id} url={show.url} />;
          }
        })}
      </div>
    </StyledWrapper>
  );
}


const StyledWrapper = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background: var(--mainbg);
  background-size: var(--mainbg-size);

  #a {
    position: sticky;
    top: 0;
    z-index: 999999;
    display: flex;
    justify-content: space-between;
    padding: 0 20px;
    background-color: #1d1d1d;
  }

  #b {
    display: flex;
    flex-wrap: wrap;
    padding: 20px;
    gap: 70px;
    align-content: flex-start;
    justify-content: space-between;
    background: transparent;
  }

  div {
    background-color: #1d1d1d;
    color: #fe4a49;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 40px;
  }

  .inside {
    border: solid #fe4a49;
    padding: 20px 20px;
    flex: 0 0 auto;
  }

  #middle {
    flex-grow: 1;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    margin-left: 20px;
    margin-right: 20px;
    gap: 20px;
  }

  .input-wrapper {
    position: relative;
    width: 200px;
    transition: width 0.3s ease;
  }

  .input-wrapper:hover {
    width: 320px;
  }

  input {
    border: solid thin #fe4a49;
    border-radius: 20px;
    background-color: #3c3c3c;
    outline: none;
    color: white;
    padding: 8px 40px 8px 12px;
    width: 100%;
    box-sizing: border-box;
    font-size: 18px;
    transition: width 0.3s ease;
  }

  i.fa-solid.fa-magnifying-glass {
    position: absolute;
    right: 12px;
    top: 50%;
    transform: translateY(-50%);
    color: #fe4a49;
    pointer-events: none;
    font-size: 20px;
  }

  .burger {
    position: relative;
    width: 40px;
    height: 30px;
    background: transparent;
    cursor: pointer;
    display: block;
  }

  .burger input {
    display: none;
  }

  .burger span {
    display: block;
    position: absolute;
    height: 4px;
    width: 100%;
    background: #fe4a49;
    border-radius: 9px;
    opacity: 1;
    left: 0;
    transform: rotate(0deg);
    transition: .25s ease-in-out;
  }

  .burger span:nth-of-type(1) {
    top: 0px;
    transform-origin: left center;
  }

  .burger span:nth-of-type(2) {
    top: 50%;
    transform: translateY(-50%);
    transform-origin: left center;
  }

  .burger span:nth-of-type(3) {
    top: 100%;
    transform-origin: left center;
    transform: translateY(-100%);
  }

  .burger input:checked ~ span:nth-of-type(1) {
    transform: rotate(45deg);
    top: 0px;
    left: 5px;
  }

  .burger input:checked ~ span:nth-of-type(2) {
    width: 0%;
    opacity: 0;
  }

  .burger input:checked ~ span:nth-of-type(3) {
    transform: rotate(-45deg);
    top: 28px;
    left: 5px;
  }

  .little {
    border: solid #fe4a49;
    padding: 20px 20px;
    flex: 0 0 auto;
  }

  .little:hover {
    background-color: #3c3c3c;
    cursor: pointer;
  }

  Card:hover {
    background-color: #fe4a49;
  }
`;


export default Homepage;
