import React, { useEffect, useRef } from "react";
import styled from "styled-components";

function Card({ url, title }) {
  const dropdownRef = useRef(null);
  const btnRef = useRef(null);

  const openInNewTab = () => {
    window.open(url, "_blank");
  };

  const getDisplayName = (fullUrl) => {
    try {
      const hostname = new URL(fullUrl).hostname;
      return hostname.replace("www.", "").split(".")[0];
    } catch {
      return "unknown";
    }
  };

  const changeIcons = (url) => {
    if (!url || typeof url !== "string") {
      return <i className="fa-solid fa-question"></i>;
    }

    try {
      const hostname = new URL(url).hostname;
      const domain = hostname.replace("www.", "").split(".")[0];

      const knownBrands = [
        "youtube",
        "github",
        "x",
        "reddit",
        "facebook",
        "linkedin",
        "instagram",
        "tiktok",
        "spotify",
      ];

      if (knownBrands.includes(domain)) {
        return <i className={`fa-brands fa-${domain}`} id="brand"></i>;
      }

      return <i className="fa-solid fa-question" id="brand"></i>;
    } catch {
      return <i className="fa-solid fa-question" id="brand"></i>;
    }
  };

  useEffect(() => {
    const btn = btnRef.current;
    const dropdown = dropdownRef.current;
    const items = dropdown.querySelectorAll(".dropdown-item");

    const handleClick = (e) => {
      e.stopPropagation();
      dropdown.classList.toggle("show");
      const isShown = dropdown.classList.contains("show");

      items.forEach((item, index) => {
        item.style.animation = "none";
        item.style.animation = `dropIn 0.3s ease forwards`;
        item.style.animationDelay = `${index * 0.05}s`;
      });
    };

    btn.addEventListener("click", handleClick);

    items.forEach((item, index) => {
      let timeout;

      item.addEventListener("mouseenter", () => {
        timeout = setTimeout(() => {
          item.classList.add("show-tooltip");
        }, 300);
      });

      item.addEventListener("mouseleave", () => {
        clearTimeout(timeout);
        item.classList.remove("show-tooltip");
      });

      if (index === 1) {
        item.addEventListener("click", () => {
          window.open("http://localhost:3000", "_blank");
        });
      }
    });

    const closeDropdown = (e) => {
      if (
        !dropdown.contains(e.target) &&
        e.target !== btn &&
        !btn.contains(e.target)
      ) {
        dropdown.classList.remove("show");
      }
    };

    window.addEventListener("click", closeDropdown);

    return () => {
      btn.removeEventListener("click", handleClick);
      window.removeEventListener("click", closeDropdown);
    };
  }, []);

  return (
    <StyledWrapper>
      <div className="wrapper" onClick={openInNewTab}>
        <div className="top">
          {changeIcons(url)}
          <button
            type="button"
            className="three-dot-button"
            ref={btnRef}
            onClick={(e) => e.stopPropagation()}
          >
            <span className="dot" />
            <span className="dot" />
            <span className="dot" />
          </button>
          <div id="dropdown-menu" className="hidden" ref={dropdownRef}>
            <div className="dropdown-item">
              <span className="tooltip">fav</span>
              <i className="fa-solid fa-heart"></i>
            </div>
            <div className="dropdown-item">
              <span className="tooltip">profile</span>
              <i className="fa-solid fa-user"></i>
            </div>
            <div className="dropdown-item">
              <span className="tooltip">delete</span>
              <i className="fa-solid fa-trash"></i>
            </div>
          </div>
        </div>
        <div className="bottom">
          <span className="icon">
            <i className="fa-solid fa-circle-play"></i>
          </span>
          <span className="showName">{getDisplayName(url)}</span>
        </div>

        <div className="sliding-info">
          <p>{title}</p>
        </div>
      </div>
    </StyledWrapper>
  );
}

const StyledWrapper = styled.div`
  position: relative;
  z-index: 1;

  .wrapper {
    width: 220px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
    transition: 0.3s ease;
    overflow: visible; /* Allow dropdown to extend */
    background-color: #1d1d1d;
    border: 1px solid #fe4a49;
  }

  .wrapper:hover {
    background-color: #fe4a49;
    transform: scale(1.1);
  }

  #dropdown-menu {
    position: absolute;
    right: 11px;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    bottom: 35px;
    gap: 5px;
    z-index: 9999;
    opacity: 0;
    pointer-events: none;
    transform: translateY(10px);
    transition: all 0.3s ease;
  }

  #dropdown-menu.show {
    opacity: 1;
    transform: translateY(0);
    pointer-events: auto;
  }

  .dropdown-item {
    position: relative;
    background-color: transparent;
    width: 10px;
    height: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 8px;
    cursor: pointer;
    transition: background-color 0.3s, color 0.3s;
    border-radius: 50%;
    border: 1px solid #fe4a49;
    color: #fe4a49;
    opacity: 0;
    transform: translateY(20px);
    animation: dropIn 0.3s ease forwards;
  }

  .dropdown-item .tooltip {
    position: absolute;
    right: 120%;
    top: 50%;
    transform: translateY(-50%);
    background-color: #fe4a49;
    color: white;
    padding: 4px 8px;
    border-radius: 6px;
    font-size: 12px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 0.5s ease;
  }

  .dropdown-item.show-tooltip .tooltip {
    opacity: 1;
  }

  .dropdown-item:nth-child(1) {
    animation-delay: 0.05s;
  }
  .dropdown-item:nth-child(2) {
    animation-delay: 0.1s;
  }
  .dropdown-item:nth-child(3) {
    animation-delay: 0.15s;
  }

  @keyframes dropIn {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .dropdown-item:hover {
    background-color: #fe4a49;
    color: white;
  }

  #brand {
    font-size: 2em;
    transition: 0.3s ease;
  }

  #brand:hover {
    transform: scale(1.1);
  }

  .top,
  .bottom {
    position: relative;
    font-size: 18px;
    margin: 3px;
    width: 100%;
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    text-align: center;
    padding: 5px;
    overflow: hidden;
    background-color: #1d1d1d;
  }

  .icon {
    margin-right: 8px;
    color: #fe4a49;
  }

  .three-dot-button {
    position: absolute;
    top: 10px;
    right: 5px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 2px;
    width: 24px;
    height: 24px;
    background: transparent;
    border: none;
    cursor: pointer;
    padding: 0;
    transition: 0.3s ease;
    z-index: 10000;
  }

  .three-dot-button:hover {
    transform: scale(1.2);
  }

  .dot {
    width: 6px;
    height: 6px;
    background-color: #fe4a49;
    border-radius: 50%;
  }

  .sliding-info {
    max-height: 0;
    overflow: hidden;
    opacity: 0;
    transition: max-height 0.4s ease, opacity 0.3s ease, padding 0.3s ease;
    background-color: #2b2b2b;
    color: white;
    width: 100%;
    box-sizing: border-box;
    padding: 0 10px;
    text-align: center;
  }

  .wrapper:hover .sliding-info {
    max-height: 100px;
    opacity: 1;
    padding: 10px;
  }
`;

export default Card;
