import styled from "styled-components";

function Card({ url, videoProgress }) {
  const openInNewTab = () => {
    chrome.tabs.create({ url }, (tab) => {
      chrome.runtime.sendMessage({
        type: "set-video-progress",
        tabId: tab.id,
        progress: videoProgress?.currentTime || 0,
      });
    });
  };
  const getDisplayName = (fullUrl) => {
    try {
      const hostname = new URL(fullUrl).hostname;
      return hostname.replace("www.", "").split(".")[0]; // e.g., "github"
    } catch {
      return "unknown";
    }
  };

  return (
    <StyledWrapper onClick={openInNewTab}>
      <div className="top">Saved</div>
      <div className="bottom">
        <span className="icon">
          <i className="fa-solid fa-circle-play"></i>
        </span>
        <span className="showName">{getDisplayName(url)}</span>
      </div>
    </StyledWrapper>
  );
}

const StyledWrapper = styled.div`
  width: 220px;
  height: 150px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background-color: #fe4a49;
  }

  .top, .bottom {
    font-size: 18px;
    margin: 3px;
    width: 100%;
    border: solid thin #fe4a49;
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    text-align: center;
    padding: 5px;
    overflow:hidden;
    background-color:#1d1d1d;
  }

  .icon {
    margin-right: 8px;
    color: #fe4a49;
  }
`;


export default Card;
