import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';
import { auth } from '../../firebase';

function LoginPage() {
  const navigate = useNavigate();
  const { login, signup, user } = useAuth(); // added signup
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [isLight, setIsLight] = useState(false);
  const [isRegister, setIsRegister] = useState(false); // toggle login/register

  useEffect(() => {
    console.log("Current authenticated user:", user);
    if (user) {
      navigate('/main');
    }
  }, [user, navigate]);

  const toggleTheme = () => {
    setIsLight((prev) => !prev);
    const root = document.documentElement;
    if (!isLight) {
      root.style.setProperty('--secondarybg', 'var(--secondarybg-light)');
      root.style.setProperty('--secondarybg-size', 'var(--secondarybg-light-size)');
    } else {
      root.style.setProperty('--secondarybg', 'var(--secondarybg-dark)');
      root.style.setProperty('--secondarybg-size', 'var(--secondarybg-dark-size)');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await login(username, password);

      const user = auth.currentUser;
      if (user) {
        const token = await user.getIdToken(true);
        const userId = user.uid;
        window.postMessage(
          {
            source: "trackvie-webapp",
            type: "FIREBASE_TOKEN",
            token: token,
            userId: userId,
          },
          "*"
        );
      }
    } catch (err) {
      console.error("Login failed:", err.message);
      setShowNotification(true);
      setTimeout(() => setShowNotification(false), 3000);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      console.error("Passwords do not match!");
      setShowNotification(true);
      setTimeout(() => setShowNotification(false), 3000);
      return;
    }
    try {
      await signup(username, password);
      console.log("User registered successfully!");
    } catch (err) {
      console.error("Registration failed:", err.message);
      setShowNotification(true);
      setTimeout(() => setShowNotification(false), 3000);
    }
  };

  useEffect(() => {
    const originalStyle = window.getComputedStyle(document.body).overflow;
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = originalStyle;
    };
  }, []);

  return (
    <StyledWrapper>
      <input
        type="checkbox"
        className="theme-checkbox"
        onChange={toggleTheme}
        checked={isLight}
      />
      <h3 style={{ color: '#FE4A49', fontSize: '50px', marginBottom: '20px', marginTop: '10px' }}>TrackVie</h3>
      <div className="animated-border">
        <div id='container'>
          <h2 id='login' style={{ fontWeight: 'bold' }}>
            {isRegister ? "Register" : "Login"}
          </h2>

          <form onSubmit={isRegister ? handleRegister : handleLogin} id='form'>
            <input
              id='username'
              type="email"
              placeholder="Enter Email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              id='password'
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {isRegister && (
              <input
                id='confirmPassword'
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            )}
            <button id='submitBtn' type="submit">
              <span>{isRegister ? "Register" : "Log In"}</span>
            </button>
          </form>

          <h1 style={{ fontSize: '18px', color: '#FE4A49' }}>or</h1>
          <button id='registerBtn' onClick={() => setIsRegister(!isRegister)}>
            {isRegister ? "Already have an account? Log In" : "Register"}
          </button>
        </div>
      </div>

      <div
        id='notification-credentials'
        className={showNotification ? 'show' : ''}
      >
        <div><i className="fa-solid fa-circle-exclamation"></i></div>
        <div style={{ marginLeft: '8px' }}>
          {isRegister ? "Registration failed" : "Wrong Username or Password"}
        </div>
      </div>
    </StyledWrapper>
  );
}


