import styled from "styled-components";
import { useEffect, useState, useRef, useCallback } from "react";
import { collection, getDocs, query, orderBy, limit, startAfter } from "firebase/firestore";
import { db } from "/Users/bedrishwayze/Desktop/Freevie/trackvie/Web/src/firebase.js";
import Card from "./Card";

const PAGE_SIZE = 10; // Load 10 items at a time

function Homepage() {
  const [shows, setShows] = useState([]);
  const [lastDoc, setLastDoc] = useState(null);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  const observer = useRef();

  // Fetch shows with pagination
  const fetchShows = useCallback(async () => {
    if (loading || !hasMore) return;
    setLoading(true);

    try {
      const baseQuery = query(
        collection(db, "Users/id-2/savedShows"),
        orderBy("timestamp", "desc"), // Replace with a real field
        limit(PAGE_SIZE),
        ...(lastDoc ? [startAfter(lastDoc)] : [])
      );

      const snapshot = await getDocs(baseQuery);
      const newShows = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      setShows(prev => [...prev, ...newShows]);
      setLastDoc(snapshot.docs[snapshot.docs.length - 1]);
      setHasMore(snapshot.docs.length === PAGE_SIZE);
    } catch (error) {
      console.error("Failed to fetch shows:", error);
    } finally {
      setLoading(false);
    }
  }, [lastDoc, loading, hasMore]);

  useEffect(() => {
    fetchShows();
  }, [fetchShows]);

  // Observer to detect scrolling near the bottom
  const lastCardRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();

    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        fetchShows();
      }
    });

    if (node) observer.current.observe(node);
  }, [loading, hasMore, fetchShows]);

  return (
    <StyledWrapper>
      <div id="a">
        <div className="inside">
          <label className="burger" htmlFor="burger">
            <input type="checkbox" id="burger" />
            <span />
            <span />
            <span />
          </label>
        </div>
        <div className="inside" id="middle">
          <form action="">
            <div className="input-wrapper">
              <input type="text" name="searchbar" id="searchbar" />
              <i className="fa-solid fa-magnifying-glass"></i>
            </div>
          </form>
          <div className="little">k</div>
          <div className="little">l</div>
          <div className="little">m</div>
        </div>
        <div className="inside">z</div>
      </div>

      <div id="b">
        {shows.map((show, index) => {
          if (index === shows.length - 1) {
            return (
              <div key={show.id} ref={lastCardRef}>
                <Card url={show.url} />
              </div>
            );
          } else {
            return <Card key={show.id} url={show.url} />;
          }
        })}
      </div>
    </StyledWrapper>
  );
}
